/*  LineBurstThread  */import	java.awt.*;import	java.awt.event.*;import	javax.swing.*;// import	LineBurst;  // not needed; is an error in newer versions of javac?class	LineBurstThread	extends Thread{	public	String	thread_name;	public	int		xpos, ypos;	public	LineBurstThread( String name, int x, int y )	{		super(name);		thread_name = name;		xpos = x;		ypos = y;	}	public	void run()	{		LineBurst	lb = new LineBurst( thread_name, xpos, ypos, 400, 400 );		lb.setDefaultCloseOperation( JFrame.DISPOSE_ON_CLOSE );	}	public	static	void main( String args[] )	{		LineBurstThread	thread1 = new LineBurstThread("Thread 1", 50, 50);		LineBurstThread	thread2 = new LineBurstThread("Thread 2", 500, 50);		LineBurstThread	thread3 = new LineBurstThread("Thread 3", 50, 500);		LineBurstThread	thread4 = new LineBurstThread("Thread 4", 500, 500);		thread2.start();		thread3.start();		thread4.start();		thread1.start();	}}